import React from 'react'
import "./Cart.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

export default function Cart(props) {
    let arr1 = props.arr;
    const [newarr, setNewarr] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    // Initialize newarr based on props.arr
    const initialArr = props.arr.map(val => ({ ...val, quantity: 1 }));
    setNewarr(initialArr);
  }, [props.arr]);

  useEffect(() => {
    // Calculate total whenever newarr changes
    const newTotal = newarr.reduce((acc, val) => acc + (val.price * val.quantity), 0);
    setTotal(newTotal);
  }, [newarr]);

  const updateQuantity = (product, increment) => {
    setNewarr(prevArr =>
      prevArr.map(item =>
        item.product === product
          ? { ...item, quantity: item.quantity + increment }
          : item
      )
    );
  };

  const deleteProduct = (product) => {
    setNewarr(prevArr => prevArr.filter(item => item.product !== product));
  };
    function Create(name, price) {
        this.product = name;
        this.price = price;
        this.quantity = 1;
    }
    arr1.forEach((val) => {
        let obj = new Create(val.product, val.price);
        newarr.push(obj);
    })
    console.log(total);
    return (
        <main id="m1">
            {console.log(props.arr)}
            {console.log(props.arr.length)}
            {
                (props.arr.length === 0) ? <h5 id="h"><b>Product list is empty...</b></h5> : <ol>{arr1.map((val, i) => {
                    console.log(val);
                    return (<li key={i} id={val.product}><div id='d'>
                        <div>
                            <div><b>Name of produt: </b>{val.product}</div>
                            <div><b>Price: </b>{val.price}</div>
                            <div>
                                <button type="button" className="btn btn-danger" onClick={(e) => {
                                    e.preventDefault();
                                    if (parseInt(document.getElementById("button" + String(i)).textContent) > 0) {
                                        //console.log(typeof document.getElementById("button").textContent)
                                        let val3 = (parseInt(document.getElementById("button" + String(i)).textContent));
                                        val3--
                                        document.getElementById("button" + String(i)).textContent = String(val3);
                                        newarr.map((val2) => {
                                            if (val2.product === val.product) {
                                                val2["quantity"] = String(val3);
                                                return val2;
                                            }
                                            return val2;
                                        })
                                        total = 0;
                                        newarr.forEach((val) => {
                                            total += ((val.price) * (val.quantity))
                                        })
                                        console.log(total);
                                }}}>-</button>
                                <button id={"button" + String(i)} type="button" className="btn btn-warning">1</button>
                                <button type="button" className="btn btn-success" onClick={(e) => {
                                    e.preventDefault();
                                    if (parseInt(document.getElementById("button" + String(i)).textContent) >= 0) {
                                        //console.log(typeof document.getElementById("button").textContent)
                                        let val3 = (parseInt(document.getElementById("button" + String(i)).textContent));
                                        val3++
                                        document.getElementById("button" + String(i)).textContent = String(val3);
                                        newarr.map((val2) => {
                                            if (val2.product === val.product) {
                                                val2["quantity"] = String(val3);
                                                return val2;
                                            }
                                            return val2;
                                        })
                                        total = 0;
                                        newarr.forEach((val) => {
                                            total += ((val.price) * (val.quantity))
                                        })
                                        console.log(total);
                                    }
                                }}>+</button>
                            </div>
                        </div>
                        <button type="button" className="btn btn-dark" onClick={(e) => {
                            e.preventDefault();
                            arr1 = arr1.filter(val1 => (val1.product !== val.product));
                            newarr = newarr.filter(val1 => (val1.product !== val.product));
                            document.getElementById(val.product).remove();
                            total = 0;
                            newarr.forEach((val) => {
                                total += ((val.price) * (val.quantity))
                            })
                            console.log(total);
                        }}>Delete</button>
                    </div></li>)
                })}</ol>
            }
            {console.log(arr1)}
            {console.log(arr1.length)}
            {console.log(newarr)}
            <form action="/cart" method="POST">
                <div><span><b>TOTAL AMOUNT IS: Rs. </b></span><span id="t" name="value">{total}</span></div>
                <button id="btn"><b><i>GO TO CART</i></b></button>
            </form>
        </main>
    )
}
