import React, { useState, useEffect } from 'react'
import "./Cart.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

export default function Cart(props) {
    let arr1 = props.arr;
    function Create(name, price) {
        this.product = name;
        this.price = price;
        this.quantity = 1;
    }
    const [total, setTotal] = useState(0);
    const [newarr, setNewarr] = useState([]);
    arr1.forEach((val) => {
        let obj = new Create(val.product, val.price);
        setNewarr([...newarr, obj])
    })
    useEffect(() => {
        setTotal(0);
        let sum = 0;
        newarr.forEach((val) => {
            sum += ((val.price) * (val.quantity))
        })
        setTotal(sum);
    }, [newarr])
    function set(val3){

    }
    return (
        <main id="m1">
            {
                (props.arr.length === 0) ? <h5 id="h"><b>Product list is empty...</b></h5> : <ol>{arr1.map((val, i) => {
                    console.log(val);
                    return (<li key={i} id={val.product}><div id='d'>
                        <div>
                            <div><b>Name of produt: </b>{val.product}</div>
                            <div><b>Price: </b>{val.price}</div>
                            <div>
                                <button type="button" className="btn btn-danger" onClick={(e) => {
                                    e.preventDefault();
                                    if (parseInt(document.getElementById("button" + String(i)).textContent) > 0) {
                                        let val3 = (parseInt(document.getElementById("button" + String(i)).textContent));
                                        val3--
                                        document.getElementById("button" + String(i)).textContent = String(val3);

                                    }
                                }}>-</button>
                                <button id={"button" + String(i)} type="button" className="btn btn-warning">1</button>
                                <button type="button" className="btn btn-success" onClick={(e) => {
                                    e.preventDefault();
                                    if (parseInt(document.getElementById("button" + String(i)).textContent) >= 0) {
                                        let val3 = (parseInt(document.getElementById("button" + String(i)).textContent));
                                        val3++
                                        document.getElementById("button" + String(i)).textContent = String(val3);
                                        setNewarr(newarr.filter((val2) => {
                                            if (val2.product === val.product) {
                                                val2["quantity"] = String(val3);
                                                return val2;
                                            }
                                            return val2;
                                        }))
                                    }
                                }}>+</button>
                            </div>
                        </div>
                        <button type="button" className="btn btn-dark" onClick={(e) => {
                            e.preventDefault();
                            setNewarr(newarr.filter(val1 => (val1.product !== val.product)));
                            document.getElementById(val.product).remove();
                        }}>Delete</button>
                    </div></li>)
                })}</ol>
            }
            <form action="/cart" method="POST">
                <div><span><b>TOTAL AMOUNT IS: Rs. </b></span><span id="t" name="value">{total}</span></div>
                <button id="btn"><b><i>GO TO CART</i></b></button>
            </form>
        </main>
    )
}
