import './App.css';
import React from 'react';
import Cart from "./Cart";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

function App() {
  const [arr, setArr] = useState([]);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');

  function Create(name, price) {
    this.product = name;
    this.price = price;
  }
  let submit = (e) => {
    e.preventDefault();
    let name = e.currentTarget.previousElementSibling.value;
    let price = e.currentTarget.previousElementSibling.previousElementSibling.value;
    let obj = new Create(name, price);
    arr.push(obj);
    console.log(arr);
  };
  return (
    <nav className="navbar bg-body-tertiary">
      <div className="container-fluid">
        <form className="d-flex" role="search">
          <input id="a" className="form-control me-2" type="search" placeholder="Add item" aria-label="Search" />
          <input id="b" className="form-control me-2" type="search" placeholder="Add price" aria-label="Search" />
          <button className="btn btn-outline-success" type="submit" onSubmit={submit}>Add</button>
        </form>
      </div>
      <Cart arr={arr} />
    </nav>
  );
}

export default App;
