import React from 'react'
import "./Cart.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import PreFooter from './PreFooter';

export default function Cart(props) {
    let arr1 = props.arr;
    function Create(name, price){
        this.product = name;
        this.price = price;
        this.quantity = 1;
    }
    let newarr = [];
    arr1.forEach((val)=>{
        let obj = new Create(val.product, val.price);
        newarr.push(obj);
    })
    let total = 0;
    newarr.for
    return (
        <main id="m1">
            {console.log(props.arr)}
            {console.log(props.arr.length)}
            {
                (props.arr.length === 0) ? <h5 id="p"><b>Product list is empty...</b></h5> : <ol>{arr1.map((val, i) => {
                    console.log(val);
                    return (<li key={i} id={val.product}><div id='d'>
                        <div>
                            <div><b>Name of produt: </b>{val.product}</div>
                            <div><b>Price: </b>{val.price}</div>
                            <div>
                                <button type="button" className="btn btn-danger" onClick={(e)=>{
                                    e.preventDefault();
                                    if(parseInt(document.getElementById("button"+String(i)).textContent)>0){
                                    //console.log(typeof document.getElementById("button").textContent)
                                    let val3 = (parseInt(document.getElementById("button"+String(i)).textContent));
                                    val3--
                                    document.getElementById("button"+String(i)).textContent = String(val3);
                                    newarr.map((val2)=>{
                                        if(val2.product === val.product){
                                            val2["quantity"] = String(val3);
                                            return val2;
                                        }
                                        return val2;
                                    })
                                }}}>-</button>
                                <button id={"button"+String(i)} type="button" className="btn btn-warning">1</button>
                                <button type="button" className="btn btn-success" onClick={(e)=>{
                                    e.preventDefault();
                                    if(parseInt(document.getElementById("button"+String(i)).textContent)>=0){
                                    //console.log(typeof document.getElementById("button").textContent)
                                    let val3 = (parseInt(document.getElementById("button"+String(i)).textContent));
                                    val3++
                                    document.getElementById("button"+String(i)).textContent = String(val3);
                                    newarr.map((val2)=>{
                                        if(val2.product === val.product){
                                            val2["quantity"] = String(val3);
                                            return val2;
                                        }
                                        return val2;
                                    })
                                }}}>+</button>
                            </div>
                        </div>
                        <button type="button" className="btn btn-dark" onClick={(e) => {
                            e.preventDefault();
                            arr1 = arr1.filter(val1 => (val1.product !== val.product));
                            newarr = newarr.filter(val1 => (val1.product !== val.product));
                            document.getElementById(val.product).remove();
                        }}>Delete</button>
                    </div></li>)
                })}</ol>
            }
            {console.log(arr1)}
            {console.log(arr1.length)}
            {console.log(newarr)}
            <PreFooter total={total}/>
        </main>
    )
}
